import React from 'react';
import {Link} from 'react-router-dom'
import logPasBase from '.././logPasBase'
import './css/Auth.css'

class Auth extends React.Component {
  state = {
    login: "",
    password: "",
    checkLogPas: false
  }
  onChangeLogin = event => {
    this.setState({ login: event.target.value });
  }
  onChangePassword = event => {
    this.setState({ password: event.target.value });
  }
  signIn = (field) => event => {
    for (var i=0; i<logPasBase.length; i++) {
      if (this.state.login===logPasBase[i].login && this.state.password===logPasBase[i].password)
      {
        this.setState({ [field]: true })
      }
    }
  }
  render() {
    console.log(this.state.login)
    let route="/auth"
    if (this.state.checkLogPas) {route="/"}
    return (
      <div className="Auth">
        <div className="logo"></div>
        <div className="header">Вход</div>
        <div className="miniheader">Логин</div>
        <div className="inputLayout">
          <input
            className="autorization"
            type="text"
            name="login"
            value={this.state.login}
            onChange={this.onChangeLogin}
          >
          </input>
        </div>
        <div className="miniheader">Пароль</div>
        <div className="inputLayout">
          <input
            className="autorization"
            type="text"
            name="password"
            value={this.state.password}
            onChange={this.onChangePassword}
          >
          </input>
        </div>
        <div className="buttonLayout" checked={this.state.checkLogPas} onClick={this.signIn("checkLogPas")}>
          <Link className="linkSignIn" to={route}>
            Вход
          </Link>
        </div>
      </div>
    )
  }
}

export default Auth;
