import React from 'react';
import {Link} from 'react-router-dom'
import logPasBase from '.././logPasBase'
import './css/Auth.css'

import Logo from './component/Logo'
import Header from './component/Header'
import Login from './component/Login'

class Auth extends React.Component {
  state = {
    login: "",
    password: "",
    button: false
  }
  onChangeLogin = event => {
    this.setState({ login: event.target.value });
  }
  onChangePassword = event => {
    this.setState({ password: event.target.value });
  }
  signIn = event => {
    this.setState({button: true})
  }
  render() {
    return (
      <div className="Auth">
        <Logo />
        <Header />
        <Login login={this.state.login} onChangeLogin={this.onChangeLogin}/>
        <div className="miniheader">Пароль</div>
        <div className="inputLayout">
          <input
            className="autorization"
            type="text"
            name="password"
            value={this.state.password}
            onChange={this.onChangePassword}
          >
          </input>
        </div>
        <div className="buttonLayout">
          <Link className="linkSignIn" to="/" checked={this.state.button} onClick={this.signIn}>
            Вход
          </Link>
        </div>
      </div>
    )
  }
}

export default Auth;
