import React from 'react';
import './css/Home.css'

import Header from './components/Header'
import SearchLine from './components/SearchLine'

const api_key="AIzaSyCZEOSYuoXj68k6aMpz78UuN2bMBwFRLiU"
const max_result=3

class Home extends React.Component {
  state = {
    menuNavigationHome: true,
    menuNavigationFavorites: false,
    searchQuery: "",
    /*channelTitle: [],
    title: [],
    url: [],*/
    videoList: [{channelTitle: "", title: "", url: ""}]
  }
  changeHome = event => {
    this.setState({menuNavigationHome: true, menuNavigationFavorites: false})
  }
  changeFavorites = event => {
    this.setState({menuNavigationHome: false, menuNavigationFavorites: true})
  }
  inputSearchQuery = event => {
    this.setState({ searchQuery: event.target.value });
  }
  getListVideos = async (event) => {
    event.preventDefault()
    let searchQuery = this.state.searchQuery
    const apiUrl = await fetch(`https://cors-anywhere.herokuapp.com/https://www.googleapis.com/youtube/v3/search?part=snippet&q=${searchQuery}&type=video&maxResults=${max_result}&key=${api_key}`)
    const data = await apiUrl.json()
    const items=data.items
    const channelTitle = []
    const title = []
    const url = []
    for (var i=0; i<items.length; i++) {
      this.setState({channelTitle: items[i].snippet.channelTitle})
      /*title[i]=items[i].snippet.title
      url[i]=items[i].snippet.thumbnails.medium.url*/
    }

  }


  render () {
    console.log(this.state.videoList)
    return (
      <div className="Home">
        <Header
          menuNavigationHome={this.state.menuNavigationHome}
          menuNavigationFavorites={this.state.menuNavigationFavorites}
          changeHome={this.changeHome}
          changeFavorites={this.changeFavorites}
        />
        <div className="body">
          <div className="header">
            Поиск видео
          </div>
            <SearchLine
              searchQuery={this.state.searchQuery}
              inputSearchQuery={this.inputSearchQuery}
              getListVideos={this.getListVideos}
            />
            <div className="video">
            </div>
        </div>
      </div>
    )
  }
}

export default Home;
