import React from 'react';
import {Link} from 'react-router-dom'
import './css/Home.css'
import Video from './Video'

const api_key="AIzaSyDmMWnY0zzXyao-vIjwsFG5HPu7dVv3WK4"
const max_result=10

class Home extends React.Component {
  state = {
    checkLogPas: false,
    menuNavigationHome: true,
    menuNavigationFavorites: false,
    searchQuery: undefined,
    videoId: []
  }
  signOut = (field) => event => {
    this.setState({ [field]: false })
    delete localStorage["token"]
  }
  changeHome = event => {
    this.setState({menuNavigationHome: true, menuNavigationFavorites: false})
  }
  changeFavorites = event => {
    this.setState({menuNavigationHome: false, menuNavigationFavorites: true})
  }
  gettingVideoId = async (e) => {
    e.preventDefault()
    const searchQuery = e.target.elements.searchQuery.value
    const videoList = []
    if (searchQuery) {
      const apiUrl = await fetch(`https://cors-anywhere.herokuapp.com/https://www.googleapis.com/youtube/v3/search?part=id&q=${searchQuery}&type=video&maxResults=${max_result}&key=${api_key}`)
      const data = await apiUrl.json()
      for (var i=0; i<max_result; i++) {
      this.setState({videoId: data.items[i].id.videoId})
      }
    }
  }
  render () {
    return (
      <div className="Home">
        <div className="header">
          <div className="logo">
          </div>
          <div className={this.state.menuNavigationHome ? "linkLayout linkLayout--active" : "linkLayout"} checked={this.state.menuNavigationHome} onClick={this.changeHome}>
            <Link to="/" className="link">Главная</Link>
          </div>
          <div className={this.state.menuNavigationFavorites ? "linkLayout linkLayout--active" : "linkLayout"} checked={this.state.menuNavigationFavorites} onClick={this.changeFavorites}>
            <Link to="/favorites" className="link">Избранное</Link>
          </div>
          <div>
          </div>
          <div className="linkLayout">
            <Link className="link" to="/auth" checked={this.state.checkLogPas} onClick={this.signOut("checkLogPas")}>Выйти</Link>
          </div>
          <div>
          </div>
        </div>
        <div className="body">
          <div className="header">
            Поиск видео
          </div>
            <Video userMethod={this.gettingVideoId}/>
          <div className="video">
          </div>
        </div>
      </div>
    )
  }
}

export default Home;
