import React from 'react';
import {Link} from 'react-router-dom'
import logPasBase from '.././logPasBase'
import './css/Auth.css'

class Auth extends React.Component {
  state = {
    login: "",
    password: "",
    checkLogPas: false
  }
  textInputChange = (field) => event => {
    this.setState({ [field]: event.target.value });
  }
  signIn = (field) => event => {
    for (var i=0; i<logPasBase.length; i++) {
      if (this.state.login===logPasBase[i].login && this.state.password===logPasBase[i].password)
      {
        this.setState({ [field]: true })
        var rand = function() {return Math.random().toString(36).substr(2)}
        let token = rand();
        logPasBase[i].token=token
        JSON.stringify(logPasBase)
      }
    }
  }
  render() {
    var localValue = localStorage.getItem('token');
    console.log(localValue);
    console.log(JSON.stringify(logPasBase))
    let route="/auth"
    if (this.state.checkLogPas) {route="/"}
    return (
      <div className="Auth">
        <div className="logo"></div>
        <div className="header">Вход</div>
        <div className="miniheader">Логин</div>
        <div className="inputLayout">
          <input className="autorization" type="text" value={this.state.login} onChange={this.textInputChange("login")}></input>
        </div>
        <div className="miniheader">Пароль</div>
        <div className="inputLayout">
          <input className="autorization" type="password" value={this.state.password} onChange={this.textInputChange("password")}></input>
        </div>
        <div className="buttonLayout" checked={this.state.checkLogPas} onClick={this.signIn("checkLogPas")}>
          <Link className="linkSignIn" to={route}>
            Вход
          </Link>
        </div>
      </div>
    )
  }
}

export default Auth;
