import React from 'react';
import {Link} from 'react-router-dom'
import logPasBase from '.././logPasBase'
import './css/Auth.css'

class Auth extends React.Component {
  state = {
    login: "",
    password: "",
    checkLogPas: false,
    route: "/auth"
  }
  onChangeLogin = event => {
    this.setState({ login: event.target.value });
  }
  onChangePassword = event => {
    this.setState({ password: event.target.value });
  }
  signIn = (field) => event => {
    const login = this.state.login
    const password = this.state.password
    if (login==="admin" && password==="admin") {this.setState({checkLogPas: true, route: "/"})} else {this.setState({checkLogPas: false, route: "/auth"})}
  }
  render() {
    console.log(this.state.checkLogPas)
    let route=this.state.route
    if (this.state.checkLogPas) {route="/"}
    return (
      <div className="Auth">
        <div className="logo"></div>
        <div className="header">Вход</div>
        <div className="miniheader">Логин</div>
        <div className="inputLayout">
          <input
            className="autorization"
            type="text"
            name="login"
            value={this.state.login}
            onChange={this.onChangeLogin}
          >
          </input>
        </div>
        <div className="miniheader">Пароль</div>
        <div className="inputLayout">
          <input
            className="autorization"
            type="text"
            name="password"
            value={this.state.password}
            onChange={this.onChangePassword}
          >
          </input>
        </div>
        <div className="buttonLayout" checked={this.state.checkLogPas} onClick={this.signIn("checkLogPas")}>
          <Link className="linkSignIn" to={route}>
            Вход
          </Link>
        </div>
      </div>
    )
  }
}

export default Auth;
