import React from 'react';
import {Link} from 'react-router-dom'
import './css/Home.css'
import VideoSearchLine from './Video/VideoSearchLine'
import VideoOutput from './Video/VideoOutput'

const api_key="AIzaSyDmMWnY0zzXyao-vIjwsFG5HPu7dVv3WK4"
const max_result=5
let video123=[]

class Home extends React.Component {
  state = {
    checkLogPas: false,
    menuNavigationHome: true,
    menuNavigationFavorites: false,
    searchQuery: "",
    inputSearchQuery: "",
    videoTittle: [],
    videoName: [],
    videoImage: [],
    totalResults: "",
    indicatorButtonSearch: false,
  }
  clickButton = (field) => event => {
    this.setState({ [field]: true })
    this.setState({inputSearchQuery: this.state.searchQuery})
  }
  signOut = (field) => event => {
    this.setState({ [field]: false })
    delete localStorage["token"]
  }
  changeHome = event => {
    this.setState({menuNavigationHome: true, menuNavigationFavorites: false})
  }
  changeFavorites = event => {
    this.setState({menuNavigationHome: false, menuNavigationFavorites: true})
  }
  textInputChange = (field) => event => {
    this.setState({ [field]: event.target.value });
  }
  gettingVideoId = async (e) => {
    e.preventDefault()
    const searchQuery = e.target.elements.searchQuery.value
    const videoList = []
    if (searchQuery) {
      const apiUrl = await fetch(`https://cors-anywhere.herokuapp.com/https://www.googleapis.com/youtube/v3/search?part=id&q=${searchQuery}&type=video&maxResults=${max_result}&key=${api_key}`)
      const data = await apiUrl.json()
      this.setState({totalResults: data.pageInfo.totalResults})
      for (var i = 0; i<max_result; i++) {
        videoList[i]=data.items[i].id.videoId
        let apiUrl2 = await fetch(`https://cors-anywhere.herokuapp.com/https://www.googleapis.com/youtube/v3/videos?part=snippet&id=${videoList[i]}&key=${api_key}`)
        const data2 = await apiUrl2.json()
        this.setState({videoTitle: data2.items[0].snippet.channelTitle,videoName: data2.items[0].snippet.title, videoImage: data2.items[0].snippet.thumbnails.medium.url})
      }
    }
  }
  render () {
    console.log(this.state.inputSearchQuery)
    return (
      <div className="Home">
        <div className="header">
          <div className="logo">
          </div>
          <div className={this.state.menuNavigationHome ? "linkLayout linkLayout--active" : "linkLayout"} checked={this.state.menuNavigationHome} onClick={this.changeHome}>
            <Link to="/" className="link">Главная</Link>
          </div>
          <div className={this.state.menuNavigationFavorites ? "linkLayout linkLayout--active" : "linkLayout"} checked={this.state.menuNavigationFavorites} onClick={this.changeFavorites}>
            <Link to="/favorites" className="link">Избранное</Link>
          </div>
          <div>
          </div>
          <div className="linkLayout">
            <Link className="link" to="/auth" checked={this.state.checkLogPas} onClick={this.signOut("checkLogPas")}>Выйти</Link>
          </div>
          <div>
          </div>
        </div>
        <div className="body">
          <div className="header">
            Поиск видео
          </div>
            <VideoSearchLine
              gettingVideoId={this.gettingVideoId}
              indicatorButtonSearch={this.state.indicatorButtonSearch}
              clickButton={this.clickButton("indicatorButtonSearch")}
              totalResults={this.state.totalResults}
              searchQuery={this.state.searchQuery}
              textInputChange={this.textInputChange("searchQuery")}
            />
            <VideoOutput
              videoTitle={this.state.videoTitle}
              videoName={this.state.videoName}
              videoImage={this.state.videoImage}
            />
        </div>
      </div>
    )
  }
}

export default Home;
