import React from 'react';
import logPasBase from '.././logPasBase'
import './css/Auth.css'

import Logo from './component/Logo'
import Header from './component/Header'
import Login from './component/Login'
import Password from './component/Password'
import ButtonSignIn from './component/ButtonSignIn'

class Auth extends React.Component {
  state = {
    login: "",
    password: "",
    authIndicator: false
  }
  onChangeLogin = event => {
    this.setState({ login: event.target.value })
  }
  onChangePassword = event => {
    this.setState({ password: event.target.value })
  }
  changeIndicatorAuth = event => {
    let json=logPasBase
    let key
    for (let i=0; i<json.length; i++) {
      if (json[i].login===this.state.login && json[i].password===this.state.password)
        {this.setState({ authIndicator: true }); key=i; break}
      else
        {this.setState({ authIndicator: false })}
    }
    logPasBase[key].token=111
  }
  render() {
    console.log(this.state.authIndicator)
    return (
      <div className="Auth">
        <Logo />
        <Header />
        <Login
          login={this.state.login}
          onChangeLogin={this.onChangeLogin}
        />
        <Password
          password={this.state.password}
          onChangePassword={this.onChangePassword}
        />
        <ButtonSignIn
          login={this.state.login}
          password={this.state.password}
          authIndicator={this.state.authIndicator}
          changeIndicatorAuth={this.changeIndicatorAuth}
        />
      </div>
    )
  }
}

export default Auth;
