import React from 'react';

class VideoSearchLine extends React.Component {
  state = {
    indicatorIntarfaceX1: true,
    indicatorIntarfaceX4: false
  }
  changeInterfaceX1 = event => {
    this.setState({indicatorIntarfaceX1: true, indicatorIntarfaceX4: false})
  }
  changeInterfaceX4 = event => {
    this.setState({indicatorIntarfaceX1: false, indicatorIntarfaceX4: true})
  }
  render () {
    return (
      <div className="searcLineLayout">
        <form onSubmit={this.props.gettingVideoId} className="form">
          <input type="text" name="searchQuery" autoComplete="off" className="string" value={this.props.searchQuery} onChange={this.props.textInputChange}/>
          <button checked={this.props.indicatorButtonSearch} onClick={this.props.clickButton} className="button">Найти</button>
        </form>
        {this.props.inputSearchQuery &&
          <div className="filterPanel">
            <div className="heading">
              Видео по запросу {this.props.searchQuery} {this.props.totalResults}
            </div>
            <div className={this.state.indicatorIntarfaceX1 ? "buttonFilterX1--active" : "buttonFilterX1"} checked={this.state.indicatorIntarfaceX1} onClick={this.changeInterfaceX1}>
            </div>
            <div className={this.state.indicatorIntarfaceX4 ? "buttonFilterX4--active" : "buttonFilterX4"} checked={this.state.indicatorIntarfaceX4} onClick={this.changeInterfaceX4}>
            </div>
          </div>
        }
      </div>
    )
  }
}

export default VideoSearchLine
