import React from 'react';
import {Link} from 'react-router-dom'

class ButtonSignIn extends React.Component {
  state = {
    loginFinal: "",
    passwordFinal: ""
  }
  render () {
    let route
    if (this.props.login==="admin" && this.props.password==="admin") {route="/"} else {route="/auth"}
    return (
      <div className="buttonLayout" onClick={this.signIn}>
        <Link className="linkSignIn" to={route}>
          Вход
        </Link>
      </div>
    )
  }
}

export default ButtonSignIn
