import React from 'react';
import {Link} from 'react-router-dom'
import './css/Home.css'
import VideoSearchLine from './Video/VideoSearchLine'
import VideoOutput from './Video/VideoOutput'

const api_key="AIzaSyDmMWnY0zzXyao-vIjwsFG5HPu7dVv3WK4"
const max_result=5

class Home extends React.Component {
  state = {
    checkLogPas: false,
    menuNavigationHome: true,
    menuNavigationFavorites: false,
    searchQuery: "",
    inputSearchQuery: "",
    videoTittle: [],
    videoName: [],
    videoImage: [],
    totalResults: "",
    indicatorButtonSearch: false,
  }
  clickButton = (field) => event => {
    this.setState({ [field]: true })
    this.setState({inputSearchQuery: this.state.searchQuery})
  }
  signOut = (field) => event => {
    this.setState({ [field]: false })
    delete localStorage["token"]
  }
  changeHome = event => {
    this.setState({menuNavigationHome: true, menuNavigationFavorites: false})
  }
  changeFavorites = event => {
    this.setState({menuNavigationHome: false, menuNavigationFavorites: true})
  }
  textInputChange = (field) => event => {
    this.setState({ [field]: event.target.value });
  }
  gettingVideoId = async (e) => {
    let arr = [];
    const apiUrl = await fetch(`https://cors-anywhere.herokuapp.com/https://www.googl..${searchQuery}&type=video&maxResults=${max_result}&key=${api_key}`)
    const data = await apiUrl.json()
    try {
      Promise.all(
      data.items.map(async (item, key) => {
        let videoInfo = await this.getVideoInfo(item.id.videoId)
        return videoInfo}))
        .then(results => {console.log(results); this.setState({listVideos: results})})
    } catch (e) {}
  }

  getVideoInfo = async (id) => {
    let apiUrl = await fetch(`https://cors-anywhere.herokuapp.com/https://www.googl..${id}&key=${api_key}`)
    let data = await apiUrl.json()
    return data
  }
  render () {
    return (
      <div className="Home">
        <div className="header">
          <div className="logo">
          </div>
          <div
            className={this.state.menuNavigationHome ? "linkLayout linkLayout--active" : "linkLayout"}
            checked={this.state.menuNavigationHome}
            onClick={this.changeHome}>
            <Link
              to="/"
              className="link"
            >
              Главная
            </Link>
          </div>
          <div
            className={this.state.menuNavigationFavorites ? "linkLayout linkLayout--active" : "linkLayout"}
            checked={this.state.menuNavigationFavorites}
            onClick={this.changeFavorites}>
            <Link
              to="/favorites"
              className="link"
            >
              Избранное
            </Link>
          </div>
          <div>
          </div>
          <div className="linkLayout">
            <Link
              className="link"
              to="/auth"
              checked={this.state.checkLogPas}
              onClick={this.signOut("checkLogPas")}
            >
              Выйти
            </Link>
          </div>
          <div>
          </div>
        </div>
        <div className="body">
          <div className="header">
            Поиск видео
          </div>
            <VideoSearchLine
              gettingVideoId={this.gettingVideoId}
              indicatorButtonSearch={this.state.indicatorButtonSearch}
              totalResults={this.state.totalResults}
              searchQuery={this.state.searchQuery}
              inputSearchQuery={this.state.inputSearchQuery}
              textInputChange={this.textInputChange("searchQuery")}
              clickButton={this.clickButton("indicatorButtonSearch")}
            />
            <VideoOutput
              videoTitle={this.state.videoTitle}
              videoName={this.state.videoName}
              videoImage={this.state.videoImage}
              inputSearchQuery={this.state.inputSearchQuery}
            />
        </div>
      </div>
    )
  }
}

export default Home;
