import React from 'react';
import './css/Home.css'
import VideoSearchLine from './components/VideoSearchLine'
import VideoOutput from './components/VideoOutput'

import Header from './components/Header'

const api_key="AIzaSyCZEOSYuoXj68k6aMpz78UuN2bMBwFRLiU"
const max_result=5

class Home extends React.Component {
  state = {
    menuNavigationHome: true,
    menuNavigationFavorites: false,
    indicatorIntarfaceX1: true,
    indicatorIntarfaceX4: false,
    searchQuery: "",
    inputSearchQuery: "",
    totalResults: "",
    indicatorButtonSearch: false,
    listVideos: ""
  }
  // Отрисовка при нажатии на кнопку
  clickButton = (field) => event => {
    this.setState({ [field]: true })
    this.setState({inputSearchQuery: this.state.searchQuery})
  }
  // Ввод текста
  textInputChange = (field) => event => {
    this.setState({ [field]: event.target.value });
  }
  // Выход в авторизацию
  signOut = (field) => event => {
    this.setState({ [field]: false })
    delete localStorage["token"]
  }
  // Переключение ссылок
  changeHome = event => {
    this.setState({menuNavigationHome: true, menuNavigationFavorites: false})
  }
  changeFavorites = event => {
    this.setState({menuNavigationHome: false, menuNavigationFavorites: true})
  }
  // Переключение интерфейса
  changeInterfaceX1 = event => {
    this.setState({indicatorIntarfaceX1: true, indicatorIntarfaceX4: false})
  }
  changeInterfaceX4 = event => {
    this.setState({indicatorIntarfaceX1: false, indicatorIntarfaceX4: true})
  }
  //Запрос на сервер
  gettingVideoId = async (e) => {
    e.preventDefault()
    const searchQuery = e.target.elements.searchQuery.value
    const apiUrl = await fetch(`https://cors-anywhere.herokuapp.com/https://www.googleapis.com/youtube/v3/search?part=id&q=${searchQuery}&type=video&maxResults=${max_result}&key=${api_key}`)
    const data = await apiUrl.json()
    try {
        Promise.all(
            data.items.map(async (item, key) => {
              let videoInfo = await this.getVideoInfo(item.id.videoId)
                return videoInfo.items[0].snippet.channelId
            })
        ).then(results => {
            this.setState({listVideos: results})
        })
    } catch (e) {}
  }

  getVideoInfo = async (id) => {
    let apiUrl = await fetch(`https://cors-anywhere.herokuapp.com/https://www.googleapis.com/youtube/v3/videos?part=snippet&id=${id}&key=${api_key}`)
    let data = await apiUrl.json()
    return data
  }
  render () {
    return (
      <div className="Home">
        <Header />
        <div className="body">
          <div className="header">
            Поиск видео
          </div>
            <VideoSearchLine
              gettingVideoId={this.gettingVideoId}
              indicatorButtonSearch={this.state.indicatorButtonSearch}
              totalResults={this.state.totalResults}
              searchQuery={this.state.searchQuery}
              inputSearchQuery={this.state.inputSearchQuery}
              textInputChange={this.textInputChange("searchQuery")}
              clickButton={this.clickButton("indicatorButtonSearch")}
              changeInterfaceX1={this.changeInterfaceX1}
              changeInterfaceX4={this.changeInterfaceX4}
              indicatorIntarfaceX1={this.state.indicatorIntarfaceX1}
              indicatorIntarfaceX4={this.state.indicatorIntarfaceX4}
            />
            <VideoOutput
              listVideos={this.state.listVideos}
              inputSearchQuery={this.state.inputSearchQuery}
            />
        </div>
      </div>
    )
  }
}

export default Home;
