import React from 'react';
import logPasBase from '.././logPasBase'
import './css/Auth.css'

import Logo from './component/Logo'
import Header from './component/Header'
import Login from './component/Login'
import Password from './component/Password'
import ButtonSignIn from './component/ButtonSignIn'

class Auth extends React.Component {
  state = {
    login: "",
    password: "",
    buttonIndicator: false
  }
  onChangeLogin = event => {
    this.setState({ login: event.target.value });
  }
  onChangePassword = event => {
    this.setState({ password: event.target.value });
  }
  signIn = event => {
    this.setState({buttonIndicator: true})
  }
  render() {
    return (
      <div className="Auth">
        <Logo />
        <Header />
        <Login login={this.state.login} onChangeLogin={this.onChangeLogin}/>
        <Password password={this.state.password} onChangePassword={this.onChangePassword}/>
        <ButtonSignIn buttonIndicator={this.state.buttonIndicator} signIn={this.signIn}/>
      </div>
    )
  }
}

export default Auth;
