import React from 'react';
import {Link} from 'react-router-dom'

class Auth extends React.Component {
  state = {
    login: "",
    password: "",
    successfullyLogin: false
  }
  render() {
    let login="admin"
    let password="admin"
    let checked=false
    let rou
    if (login==="admin" && password==="admin") {checked=true}
    if (checked) rou = "/"
    return (
      <div>
        Auth
        <Link to={rou}>Войти</Link>
      </div>
    )
  }
}

export default Auth;
