import React from 'react';
import {Link} from 'react-router-dom'
import logPasBase from '.././logPasBase'
import './css/Auth.css'

class Auth extends React.Component {
  state = {
    login: "",
    password: "",
    button: false
  }
  onChangeLogin = event => {
    this.setState({ login: event.target.value });
  }
  onChangePassword = event => {
    this.setState({ password: event.target.value });
  }
  signIn = event => {
    this.setState({button: true})
  }
  render() {
    let route="/auth"
    if (this.state.button===true) {route="/"} else {route="/auth"}
    console.log(this.state.button)
    console.log(route)
    return (
      <div className="Auth">
        <div className="logo"></div>
        <div className="header">Вход</div>
        <div className="miniheader">Логин</div>
        <div className="inputLayout">
          <input
            className="autorization"
            type="text"
            name="login"
            value={this.state.login}
            onChange={this.onChangeLogin}
          >
          </input>
        </div>
        <div className="miniheader">Пароль</div>
        <div className="inputLayout">
          <input
            className="autorization"
            type="text"
            name="password"
            value={this.state.password}
            onChange={this.onChangePassword}
          >
          </input>
        </div>
        <div className="buttonLayout" onClick={this.signIn}>
          <Link className="linkSignIn" to={route}>
            Вход
          </Link>
        </div>
      </div>
    )
  }
}

export default Auth;
